mapper.select:
select count(user_acct)
from t_sys_user
where is_deleted = 0
and user_acct = #{userAcct}

    and user_code != #{userId}

mapper.update:
update t_sys_user set is_deleted = 1, gmt_modified = now(), last_modified_by = #{userCode}, version = version + 1 where user_code in
#{userId}
and version = #{version}
